import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';
import './transactions.css'; 

const Transactions = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const token = localStorage.getItem("token"); 

    useEffect(() => {
        const fetchTransactions = async () => {
            
            if (!token) {
                setError("Vui lòng đăng nhập để xem giao dịch.");
                setLoading(false);
                return;
            }

            setLoading(true);
            
            try {
                
                const res = await axios.get(`http://localhost:5000/api/transactions/me`, { 
                     headers: { "Authorization": `Bearer ${token}` }, 
                });
                
                // Bây giờ Frontend mong đợi res.data chứa dateStart và dateEnd
                setTransactions(res.data);
                setError(null);
            } catch (err) {
                console.error("Lỗi khi lấy giao dịch:", err);
                
                if (err.response && err.response.status === 401) {
                    setError("Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.");
                } else {
                    setError("Không thể tải lịch sử giao dịch. Vui lòng kiểm tra Server.");
                }
            } finally {
                setLoading(false);
            }
        };

        fetchTransactions();
    
    }, [token]); 
    
    
    /**
     * Tính toán trạng thái dựa trên ngày hiện tại và ngày nhận/trả phòng (dateStart, dateEnd).
     * @param {string} dateStart - Ngày nhận phòng (t.dateStart)
     * @param {string} dateEnd - Ngày trả phòng (t.dateEnd)
     * @returns {string} Trạng thái đã tính toán ('Booked', 'Checkin', 'Checkout').
     */
    const getCalculatedStatus = (dateStart, dateEnd) => {
        // Kiểm tra đầu vào (để debug nếu có lỗi)
        if (!dateStart || !dateEnd) {
             console.error("Thiếu ngày bắt đầu/kết thúc cho giao dịch!");
             return 'Booked'; 
        }

        // Lấy ngày hiện tại (chỉ phần ngày, bỏ qua thời gian)
        const today = new Date();
        today.setHours(0, 0, 0, 0);

        // Chuyển đổi chuỗi ngày thành đối tượng Date để so sánh
        const checkIn = new Date(dateStart); // SỬ DỤNG dateStart
        const checkOut = new Date(dateEnd);   // SỬ DỤNG dateEnd
        
        // Kiểm tra tính hợp lệ của ngày
        if (isNaN(checkIn) || isNaN(checkOut)) {
             console.error("Ngày tháng không hợp lệ:", dateStart, dateEnd);
             return 'Booked'; 
        }

        // Thiết lập giờ về 0 để so sánh chính xác theo ngày
        checkIn.setHours(0, 0, 0, 0);
        checkOut.setHours(0, 0, 0, 0);

        // 1. Quá ngày Checkout: Là 'Checkout'
        // So sánh: Hôm nay lớn hơn ngày checkout
        if (today > checkOut) {
            return 'Checkout';
        }
        
        // 2. Trong khoảng từ Checkin đến Checkout (bao gồm cả 2 ngày): Là 'Checkin'
        // So sánh: Hôm nay >= ngày check-in VÀ Hôm nay <= ngày check-out
        if (today >= checkIn && today <= checkOut) {
            return 'Checkin';
        }
        
        // 3. Chưa đến ngày Checkin: Là 'Booked'
        return 'Booked';
    };


    /**
     * Chỉ trả về class CSS tương ứng với trạng thái.
     * @param {string} status - Trạng thái đã tính toán.
     * @returns {string} Tên class CSS.
     */
    const getStatusClass = (status) => {
        switch (status) {
            case 'Booked': return 'statusBooked';
            case 'Checkin': return 'statusCheckin';
            case 'Checkout': return 'statusCheckout';
            default: return 'statusDefault';
        }
    };
    
   

    if (loading) return (
        <>
            <Navbar />
            <p className="loading" style={{textAlign: 'center', margin: '100px'}}>Đang tải giao dịch...</p>
            <Footer />
        </>
    );

    return (
        <>
            <Navbar />
            <div className="transactionsContainer">
                <div className="transactionsWrapper">
                    <h1 className="transactionsTitle">Lịch sử Giao dịch của bạn</h1>
                    
                    {error && <p className="error" style={{color: 'red', fontWeight: 'bold'}}>{error}</p>}

                    {transactions.length === 0 && !error ? (
                        <p>Bạn chưa có giao dịch nào.</p>
                    ) : (
                        <table className="transactionsTable">
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Hotel</th>
                                    <th>Room</th>
                                    <th>Date</th>
                                    <th>Price</th>
                                    <th>Payment Method</th>
                                    <th>Status</th> 
                                </tr>
                            </thead>
                            <tbody>
                                {transactions.map((t, index) => {
                                    // *** TÍNH TOÁN TRẠNG THÁI MỚI DỰA TRÊN T.DATESTART VÀ T.DATEEND ***
                                    const currentStatus = getCalculatedStatus(t.dateStart, t.dateEnd);
                                    
                                    return (
                                        <tr key={t.id}>
                                            <td>{index + 1}</td>
                                            <td className="hotelColumn">{t.hotelName}</td>
                                            <td>{t.rooms}</td> 
                                            <td>{t.date}</td> {/* t.date: Có thể là ngày đặt phòng */}
                                            <td>{t.price}</td>
                                            <td>{t.paymentMethod}</td>
                                            <td>
                                                <span className={`statusBadge ${getStatusClass(currentStatus)}`}>
                                                    {currentStatus} {/* Hiển thị trạng thái đã tính toán */}
                                                </span>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
           <div className="footer-section">
                <Footer />
            </div>
        </>
    );
};

export default Transactions;
