import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';
// Import file CSS (ƒë√£ ƒë∆∞·ª£c s·ª≠a)
import './transactions.css'; 

const Transactions = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    // L·∫•y token tr·ª±c ti·∫øp ·ªü ƒë√¢y
    const token = localStorage.getItem("token"); 

    useEffect(() => {
        const fetchTransactions = async () => {
            
            // üõë Ki·ªÉm tra token: N·∫øu kh√¥ng c√≥ token, hi·ªÉn th·ªã l·ªói ƒëƒÉng nh·∫≠p
            if (!token) {
                setError("Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ xem giao d·ªãch.");
                setLoading(false);
                return;
            }

            setLoading(true);
            
            try {
                // G·ª≠i y√™u c·∫ßu GET t·ªõi API, s·ª≠ d·ª•ng header Authorization (Bearer Token)
                const res = await axios.get(`http://localhost:5000/api/transactions/me`, { 
                     headers: { "Authorization": `Bearer ${token}` }, 
                });
                
                setTransactions(res.data);
                setError(null);
            } catch (err) {
                console.error("L·ªói khi l·∫•y giao d·ªãch:", err);
                
                if (err.response && err.response.status === 401) {
                    setError("Phi√™n ƒëƒÉng nh·∫≠p ƒë√£ h·∫øt h·∫°n. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.");
                } else {
                    setError("Kh√¥ng th·ªÉ t·∫£i l·ªãch s·ª≠ giao d·ªãch. Vui l√≤ng ki·ªÉm tra Server.");
                }
            } finally {
                setLoading(false);
            }
        };

        fetchTransactions();
    
    }, [token]); // Dependency array: Ch·∫°y l·∫°i khi token thay ƒë·ªïi
    
    // H√†m h·ªó tr·ª£ CSS
    const getStatusClass = (status) => {
        switch (status) {
            case 'Booked': return 'statusBooked';
            case 'Checkin': return 'statusCheckin';
            case 'Checkout': return 'statusCheckout';
            default: return 'statusDefault';
        }
    };
    
    // H√†m h·ªó tr·ª£ ƒë·ªãnh d·∫°ng ti·ªÅn t·ªá
    const formatPrice = (price) => {
        if (typeof price !== 'number') return 'N/A';
        return price.toLocaleString("vi-VN"); 
    };

    if (loading) return (
        <>
            <Navbar />
            <p className="loading" style={{textAlign: 'center', margin: '100px'}}>ƒêang t·∫£i giao d·ªãch...</p>
            <Footer />
        </>
    );

    return (
        <>
            <Navbar />
            <div className="transactionsContainer">
                <div className="transactionsWrapper">
                    <h1 className="transactionsTitle">L·ªãch s·ª≠ Giao d·ªãch c·ªßa b·∫°n</h1>
                    
                    {error && <p className="error" style={{color: 'red', fontWeight: 'bold'}}>{error}</p>}

                    {transactions.length === 0 && !error ? (
                        <p>B·∫°n ch∆∞a c√≥ giao d·ªãch n√†o.</p>
                    ) : (
                        <table className="transactionsTable">
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Hotel</th>
                                    <th>Room</th>
                                    <th>Date</th>
                                    <th>Price (VND)</th>
                                    <th>Payment Method</th>
                                    <th>Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {transactions.map((t, index) => (
                                    <tr key={t.id}>
                                        <td>{index + 1}</td>
                                        <td className="hotelColumn">{t.hotelName}</td>
                                        <td>{t.rooms}</td> 
                                        <td>{t.date}</td>
                                        <td>{formatPrice(t.price)}</td>
                                        <td>{t.paymentMethod}</td>
                                        <td>
                                            <span className={`statusBadge ${getStatusClass(t.status)}`}>
                                                {t.status}
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
           <div className="footer-section">
                <Footer />
            </div>
        </>
    );
};

export default Transactions;
