import "./hotel.css";
import Navbar from "../../components/navbar/Navbar";
import Header from "../../components/header/Header";
import MailList from "../../components/mailList/MailList";
import Footer from "../../components/footer/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
Â  faCircleArrowLeft,
Â  faCircleArrowRight,
Â  faCircleXmark,
Â  faLocationDot,
} from "@fortawesome/free-solid-svg-icons";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

// ThÃªm useRef Ä‘á»ƒ cuá»™n trang
import { useRef } from "react"; 

const Hotel = () => {
Â  const { hotelID } = useParams();
Â  const [hotel, setHotel] = useState(null);
Â  const [loading, setLoading] = useState(true);

Â  const [slideNumber, setSlideNumber] = useState(0);
Â  const [open, setOpen] = useState(false);
Â  
Â  // Ref Ä‘á»ƒ cuá»™n Ä‘áº¿n form
Â  const bookingFormRef = useRef(null); 

Â  // Booking states
Â  const [showBookingForm, setShowBookingForm] = useState(false);
Â  const [bookingData, setBookingData] = useState({
Â  Â  checkIn: "",
Â  Â  checkOut: "",
Â  Â  // ğŸ›‘ roomsSelected: Máº¢NG chá»©a cÃ¡c ID phÃ²ng cá»¥ thá»ƒ (Room Number ID)
Â  Â  roomsSelected: [],
Â  Â  paymentMethod: "credit_card",
Â  });
Â  // availableRooms: Chá»©a Room Type vÃ  danh sÃ¡ch Room Number cÃ²n trá»‘ng
Â  const [availableRooms, setAvailableRooms] = useState([]);
Â  
Â  const [userInfo, setUserInfo] = useState({
Â  Â  name: "",
Â  Â  email: "",
Â  Â  phone: "",
Â  Â  identityCard: "", // Giá»¯ láº¡i trÆ°á»ng CMND/CCCD
Â  });


Â  // Fetch hotel info
Â  useEffect(() => {
Â  Â  const fetchHotel = async () => {
Â  Â  Â  try {
Â  Â  Â  Â  const res = await axios.get(`http://localhost:5000/api/hotels/${hotelID}`);
Â  Â  Â  Â  setHotel(res.data);
Â  Â  Â  } catch (err) {
Â  Â  Â  Â  console.error(err);
Â  Â  Â  Â  alert("KhÃ´ng láº¥y Ä‘Æ°á»£c thÃ´ng tin khÃ¡ch sáº¡n");
Â  Â  Â  } finally {
Â  Â  Â  Â  setLoading(false);
Â  Â  Â  }
Â  Â  };
Â  Â  fetchHotel();
Â  }, [hotelID]);

Â  // Fetch available rooms when dates change
Â  useEffect(() => {
Â  Â  const fetchAvailableRooms = async () => {
Â  Â  Â  if (!bookingData.checkIn || !bookingData.checkOut) {
Â  Â  Â  Â  setAvailableRooms([]);
Â  Â  Â  Â  return;
Â  Â  Â  }
Â  Â  Â  
Â  Â  Â  // Äáº£m báº£o ngÃ y checkout pháº£i lá»›n hÆ¡n ngÃ y checkin
Â  Â  Â  if (new Date(bookingData.checkIn) >= new Date(bookingData.checkOut)) {
Â  Â  Â  Â  Â  setAvailableRooms([]);
Â  Â  Â  Â  Â  setBookingData(prev => ({ ...prev, roomsSelected: [] })); // Reset phÃ²ng Ä‘Ã£ chá»n
Â  Â  Â  Â  Â  return;
Â  Â  Â  }

Â  Â  Â  try {
Â  Â  Â  Â  // ğŸ›‘ Endpoint API Ä‘Ãºng Ä‘á»ƒ kiá»ƒm tra phÃ²ng trá»‘ng
Â  Â  Â  Â  const res = await axios.get(
Â  Â  Â  Â  Â  `http://localhost:5000/api/hotels/room/checkAvailable`, // Giáº£ Ä‘á»‹nh Ä‘Ã¢y lÃ  endpoint Ä‘Ãºng
Â  Â  Â  Â  Â  {
Â  Â  Â  Â  Â  Â  params: {
Â  Â  Â  Â  Â  Â  Â  hotelId: hotelID,
Â  Â  Â  Â  Â  Â  Â  startDate: bookingData.checkIn,
Â  Â  Â  Â  Â  Â  Â  endDate: bookingData.checkOut,
Â  Â  Â  Â  Â  Â  },
Â  Â  Â  Â  Â  }
Â  Â  Â  Â  );
Â  Â  Â  Â  setAvailableRooms(res.data);
Â  Â  Â  } catch (err) {
Â  Â  Â  Â  console.error("Lá»—i láº¥y phÃ²ng trá»‘ng:", err);
Â  Â  Â  Â  setAvailableRooms([]);
Â  Â  Â  }
Â  Â  };
Â  Â  fetchAvailableRooms();
Â  Â  
Â  Â  // Reset roomsSelected khi ngÃ y thay Ä‘á»•i
Â  Â  setBookingData(prev => ({ ...prev, roomsSelected: [] }));
Â  Â  
Â  }, [bookingData.checkIn, bookingData.checkOut, hotelID]);

Â  const handleOpen = (i) => {
Â  Â  setSlideNumber(i);
Â  Â  setOpen(true);
Â  };

Â  const handleMove = (direction) => {
Â  Â  if (!hotel || !hotel.photos) return;

Â  Â  let newSlideNumber;
Â  Â  if (direction === "l") {
Â  Â  Â  newSlideNumber = slideNumber === 0 ? hotel.photos.length - 1 : slideNumber - 1;
Â  Â  } else {
Â  Â  Â  newSlideNumber = slideNumber === hotel.photos.length - 1 ? 0 : slideNumber + 1;
Â  Â  }
Â  Â  setSlideNumber(newSlideNumber);
Â  };

Â  // ğŸ›‘ HÃ€M Má»šI: Xá»­ lÃ½ viá»‡c chá»n/bá» chá»n ID cá»§a Tá»ªNG PHÃ’NG Cá»¤ THá»‚
Â  const handleSelectRoom = (roomId, checked) => {
Â  Â  let newRooms = [...bookingData.roomsSelected];
Â  Â  if (checked) {
Â  Â  Â  newRooms.push(roomId);
Â  Â  } else {
Â  Â  Â  newRooms = newRooms.filter((id) => id !== roomId);
Â  Â  }
Â  Â  setBookingData({ ...bookingData, roomsSelected: newRooms });
Â  };

Â  // ğŸ›‘ HÃ€M Má»šI: TÃ­nh tá»•ng tiá»n dá»±a trÃªn cÃ¡c Room ID Ä‘Ã£ chá»n
Â  const calculateTotal = () => {
Â  Â  if (!bookingData.checkIn || !bookingData.checkOut) return 0;
Â  Â  
Â  Â  const start = new Date(bookingData.checkIn);
Â  Â  const end = new Date(bookingData.checkOut);
Â  Â  
Â  Â  if (end <= start) return 0;
Â  Â  
Â  Â  const diffTime = Math.abs(end - start);
Â  Â  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
Â  Â  
Â  Â  let totalRoomPrice = 0;
Â  Â  
Â  Â  // Láº·p qua Táº¤T Cáº¢ cÃ¡c loáº¡i phÃ²ng cÃ³ sáºµn (availableRooms)
Â  Â  availableRooms.forEach(roomType => {
Â  Â  Â  Â  // Láº·p qua Táº¤T Cáº¢ cÃ¡c phÃ²ng cá»¥ thá»ƒ (roomNumber) trong loáº¡i phÃ²ng Ä‘Ã³
Â  Â  Â  Â  roomType.roomNumbers.forEach(roomNumber => {
Â  Â  Â  Â  Â  Â  // Náº¿u ID phÃ²ng cá»¥ thá»ƒ nÃ y Ä‘Ã£ Ä‘Æ°á»£c chá»n trong bookingData.roomsSelected
Â  Â  Â  Â  Â  Â  if (bookingData.roomsSelected.includes(roomNumber._id)) { 
Â  Â  Â  Â  Â  Â  Â  Â  // Cá»™ng thÃªm giÃ¡ cá»§a loáº¡i phÃ²ng (roomType.price)
Â  Â  Â  Â  Â  Â  Â  Â  totalRoomPrice += roomType.price;
Â  Â  Â  Â  Â  Â  }
Â  Â  Â  Â  });
Â  Â  });
Â  Â  
Â  Â  return totalRoomPrice * diffDays;
Â  };
Â  
Â  // HÃ m xá»­ lÃ½ nÃºt Reserve/Book Now
Â  const handleBookNow = () => {
Â  Â  Â  setShowBookingForm(true);
Â  Â  Â  setTimeout(() => {
Â  Â  Â  Â  Â  if (bookingFormRef.current) {
Â  Â  Â  Â  Â  Â  Â  bookingFormRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
Â  Â  Â  Â  Â  }
Â  Â  Â  }, 100); 
Â  };
Â  

Â  const handleReserve = async () => {
Â  Â  const allSelectedRoomIds = bookingData.roomsSelected;

Â  Â  if (!bookingData.checkIn || !bookingData.checkOut || allSelectedRoomIds.length === 0) {
Â  Â  Â  return alert("Vui lÃ²ng chá»n ngÃ y vÃ  Ã­t nháº¥t má»™t phÃ²ng!");
Â  Â  }
Â  Â  if (!userInfo.name || !userInfo.email || !userInfo.phone) {
Â  Â  Â  Â  return alert("Vui lÃ²ng Ä‘iá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin cÃ¡ nhÃ¢n (TÃªn, Email, Äiá»‡n thoáº¡i)!");
Â  Â  }
Â  Â  
Â  Â  try {
Â  Â  Â  await axios.post("http://localhost:5000/api/bookings", {
Â  Â  Â  Â  hotelID,
Â  Â  Â  Â  user: userInfo,
Â  Â  Â  Â  rooms: allSelectedRoomIds, 
Â  Â  Â  Â  checkIn: bookingData.checkIn,
Â  Â  Â  Â  checkOut: bookingData.checkOut,
Â  Â  Â  Â  paymentMethod: bookingData.paymentMethod,
Â  Â  Â  Â  totalPrice: calculateTotal(),
Â  Â  Â  });
Â  Â  Â  alert("Booking thÃ nh cÃ´ng!");
Â  Â  Â  window.location.href = "/transactions";
Â  Â  } catch (err) {
Â  Â  Â  console.error(err);
Â  Â  Â  alert("Lá»—i Ä‘áº·t phÃ²ng. Vui lÃ²ng kiá»ƒm tra láº¡i thÃ´ng tin hoáº·c phÃ²ng Ä‘Ã£ bá»‹ Ä‘áº·t.");
Â  Â  }
Â  };

Â  if (loading) return <p style={{ textAlign: "center", padding: 50 }}>Äang táº£i...</p>;
Â  if (!hotel) return <p style={{ textAlign: "center", padding: 50 }}>KhÃ´ng tÃ¬m tháº¥y khÃ¡ch sáº¡n</p>;

Â  return (
Â  Â  <>
Â  Â  Â <Navbar/>
Â  Â  Â <Header type="list"/>
Â  Â  Â  <div className="hotelContainer">
Â  Â  Â  Â  {open && (
Â  Â  Â  Â  Â  <div className="slider">
Â  Â  Â  Â  Â  Â  <FontAwesomeIcon
Â  Â  Â  Â  Â  Â  Â  icon={faCircleXmark}
Â  Â  Â  Â  Â  Â  Â  className="close"
Â  Â  Â  Â  Â  Â  Â  onClick={() => setOpen(false)}
Â  Â  Â  Â  Â  Â  />
Â  Â  Â  Â  Â  Â  <FontAwesomeIcon
Â  Â  Â  Â  Â  Â  Â  icon={faCircleArrowLeft}
Â  Â  Â  Â  Â  Â  Â  className="arrow"
Â  Â  Â  Â  Â  Â  Â  onClick={() => handleMove("l")}
Â  Â  Â  Â  Â  Â  />
Â  Â  Â  Â  Â  Â  <div className="sliderWrapper">
Â  Â  Â  Â  Â  Â  Â  <img
Â  Â  Â  Â  Â  Â  Â  Â  src={hotel.photos[slideNumber]}
Â  Â  Â  Â  Â  Â  Â  Â  alt={hotel.name}
Â  Â  Â  Â  Â  Â  Â  Â  className="sliderImg"
Â  Â  Â  Â  Â  Â  Â  />
Â  Â  Â  Â  Â  Â  </div>
Â  Â  Â  Â  Â  Â  <FontAwesomeIcon
