import { useState, useEffect } from "react";
import "./featured.css";

const Featured = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Đảm bảo tên hiển thị có dấu, ảnh phải nằm trong thư mục public
    const cityData = [
        { city: "Hà Nội", img: "/images/city/Ha Noi.jpg" },
        { city: "Hồ Chí Minh", img: "/images/city/HCM.jpg" },
        { city: "Đà Nẵng", img: "/images/city/Da Nang.jpg" },
    ];

    useEffect(() => {
        const fetchData = async () => {
            try {

                const res = await fetch("http://localhost:5000/api/hotels/countByCity"); 

                if (!res.ok) {

                    const errorDetails = await res.text().catch(() => 'No error body');
                    throw new Error(`API failed with status ${res.status}. Details: ${errorDetails.substring(0, 50)}`);
                }
                const apiData = await res.json();

                // Kết hợp dữ liệu API (count) với dữ liệu hình ảnh
                const combinedData = cityData.map(cityItem => {
                    // Logic tìm kiếm: So sánh tên thành phố có dấu (của cityItem) với tên thành phố trả về từ API
                    // LƯU Ý: Nếu API trả về tên KHÔNG DẤU, bạn cần chuẩn hóa ở đây HOẶC sửa Controller.
                    const found = apiData.find(item => item.city === cityItem.city);

                    return {
                        ...cityItem,
                        count: found ? found.count : 0 
                    };
                });

                setData(combinedData);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) {
        return <div className="featured">Đang tải dữ liệu thành phố...</div>;
    }

    if (error) {
        return <div className="featured">Lỗi tải dữ liệu: {error}</div>;
    }

    if (data.length === 0) {
        return <div className="featured">Không có dữ liệu khách sạn theo khu vực.</div>;
    }

    return (
       <div className="featured">
           {data.map((item, index) => (
               <div className="featuredItem" key={index}>
                   <img
                       src={item.img} 
                       alt={item.city}
                       className="featuredImg"
                   />
                   <div className="featuredTitles">
                       <h1>{item.city}</h1>
                       <h2>{item.count} properties</h2>
                   </div>
               </div>
           ))}
       </div>
    );
};

export default Featured;
