// pages/HotelsPage.js
import { useLocation } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function HotelsPage() {
  const location = useLocation();
  const searchData = location.state; // Lấy từ navigate

  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!searchData) {
      setLoading(false);
      return;
    }

    const fetchHotels = async () => {
      try {
        setLoading(true);
        const res = await axios.get('http://localhost:5000/api/hotels/search', {
          params: {
            city: searchData.destination,
            startDate: searchData.startDate,
            endDate: searchData.endDate,
            adults: searchData.adults,
            children: searchData.children,
            rooms: searchData.rooms,
          },
        });
        setHotels(res.data.results || []);
      } catch (err) {
        console.error("Lỗi tìm kiếm:", err);
        alert("Không thể tải kết quả. Vui lòng thử lại.");
      } finally {
        setLoading(false);
      }
    };

    fetchHotels();
  }, [searchData]);

  // Nếu không có dữ liệu tìm kiếm
  if (!searchData) {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>
        <h3>Không có kết quả tìm kiếm</h3>
        <p>Vui lòng thực hiện tìm kiếm từ trang chủ.</p>
        <button onClick={() => window.history.back()} className="headerBtn">
          Quay lại
        </button>
      </div>
    );
  }

  if (loading) {
    return <div style={{ textAlign: 'center', padding: '50px' }}>Đang tìm khách sạn...</div>;
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      {/* Hiển thị tiêu chí tìm kiếm */}
      <div style={{ marginBottom: '20px', padding: '15px', background: '#f8f9fa', borderRadius: '8px' }}>
        <h2>
          Kết quả tại: <strong>{searchData.destination}</strong>
        </h2>
        <p>
          {searchData.startDate} → {searchData.endDate} | 
          {searchData.adults} người lớn, {searchData.children} trẻ em, {searchData.rooms} phòng
        </p>
      </div>


      {hotels.length === 0 ? (
        <p>Không tìm thấy khách sạn nào phù hợp.</p>
      ) : (
        <div style={{
          display: 'grid',
          gap: '20px',
          gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))'
        }}>
          {hotels.map(hotel => (
            <div key={hotel._id} style={{
              border: '1px solid #ddd',
              borderRadius: '12px',
              overflow: 'hidden',
              boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
            }}>
              {hotel.photos?.[0] ? (
                <img
                  src={hotel.photos[0]}
                  alt={hotel.name}
                  style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                />
              ) : (
                <div style={{ height: '200px', background: '#f0f0f0', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  <span>Không có ảnh</span>
                </div>
              )}
              <div style={{ padding: '16px' }}>
                <h3 style={{ margin: '0 0 8px', fontSize: '18px' }}>{hotel.name}</h3>
                <p style={{ margin: '4px 0', color: '#555' }}>
                  {hotel.city} • {hotel.distance}
                </p>
                {hotel.rating > 0 && (
                  <p style={{ margin: '4px 0', color: '#febb02', fontWeight: 'bold' }}>
                    Rating: {hotel.rating}/5
                  </p>
                )}
                <p style={{ margin: '8px 0', fontWeight: 'bold', color: '#003580' }}>
                  ${hotel.cheapestPrice} / đêm
                </p>
                <button
                  onClick={() => window.location.href = `/hotel/${hotel._id}`}
                  style={{
                    background: '#febb02',
                    color: '#003580',
                    border: 'none',
                    padding: '10px',
                    width: '100%',
                    borderRadius: '6px',
                    fontWeight: 'bold',
                    cursor: 'pointer'
                  }}
                >
                  Xem chi tiết
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
