import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Hotel = () => {
  const { hotelID } = useParams(); 
  const [hotel, setHotel] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHotel = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/hotels/${hotelID}`);
        setHotel(res.data);
      } catch (err) {
        console.error(err);
        alert("Không lấy được thông tin khách sạn");
      } finally {
        setLoading(false);
      }
    };

    fetchHotel();
  }, [hotelID]);

  if (loading) return <p>Loading...</p>;
  if (!hotel) return <p>Không tìm thấy khách sạn</p>;

  return (
    <div>
      <h1>{hotel.name}</h1>
      <p>Địa chỉ: {hotel.address}</p>
      <p>Thành phố: {hotel.city}</p>
      <p>Giá thấp nhất: ${hotel.cheapestPrice}</p>
      <p>Đánh giá: {hotel.rating}</p>
      <p>Mô tả: {hotel.desc}</p>


      <div>
        {hotel.photos && hotel.photos.map((photo, i) => (
          <img key={i} src={photo} alt={hotel.name} style={{ width: 200, margin: 5 }} />
        ))}
      </div>
    </div>
  );
};

export default Hotel;
