import "./hotel.css";
import Navbar from "../../components/navbar/Navbar";
import Header from "../../components/header/Header";
import MailList from "../../components/mailList/MailList";
import Footer from "../../components/footer/Footer";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleArrowLeft,
  faCircleArrowRight,
  faCircleXmark,
  faLocationDot,
} from "@fortawesome/free-solid-svg-icons";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Hotel = () => {
  const { hotelID } = useParams();
  const [hotel, setHotel] = useState(null);
  const [loading, setLoading] = useState(true);

  const [slideNumber, setSlideNumber] = useState(0);
  const [open, setOpen] = useState(false);

  // Booking states
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingData, setBookingData] = useState({
    checkIn: "",
    checkOut: "",
    roomsSelected: [],
    paymentMethod: "credit_card",
  });
  const [availableRooms, setAvailableRooms] = useState([]);
  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
    phone: "",
  });

  // Fetch hotel info
  useEffect(() => {
    const fetchHotel = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/hotels/${hotelID}`);
        setHotel(res.data);
      } catch (err) {
        console.error(err);
        alert("Không lấy được thông tin khách sạn");
      } finally {
        setLoading(false);
      }
    };
    fetchHotel();
  }, [hotelID]);

  // Fetch available rooms when dates change
  useEffect(() => {
    const fetchAvailableRooms = async () => {
      if (!bookingData.checkIn || !bookingData.checkOut) return;
      try {
        const res = await axios.get(
          `http://localhost:5000/api/hotels/${hotelID}/availableRooms`,
          {
            params: {
              startDate: bookingData.checkIn,
              endDate: bookingData.checkOut,
            },
          }
        );
        setAvailableRooms(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAvailableRooms();
  }, [bookingData.checkIn, bookingData.checkOut, hotelID]);

  const handleOpen = (i) => {
    setSlideNumber(i);
    setOpen(true);
  };

  const handleMove = (direction) => {
    if (!hotel || !hotel.photos) return;

    let newSlideNumber;
    if (direction === "l") {
      newSlideNumber = slideNumber === 0 ? hotel.photos.length - 1 : slideNumber - 1;
    } else {
      newSlideNumber = slideNumber === hotel.photos.length - 1 ? 0 : slideNumber + 1;
    }
    setSlideNumber(newSlideNumber);
  };

  const handleSelectRoom = (room, checked) => {
    let newRooms = [...bookingData.roomsSelected];
    if (checked) {
      newRooms.push(room);
    } else {
      newRooms = newRooms.filter((r) => r._id !== room._id);
    }
    setBookingData({ ...bookingData, roomsSelected: newRooms });
  };

  const calculateTotal = () => {
    if (!bookingData.checkIn || !bookingData.checkOut) return 0;
    const start = new Date(bookingData.checkIn);
    const end = new Date(bookingData.checkOut);
    const diffDays = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
    const roomTotal = bookingData.roomsSelected.reduce((sum, room) => sum + room.price, 0);
    return roomTotal * diffDays;
  };

  const handleReserve = async () => {
    if (!bookingData.checkIn || !bookingData.checkOut || bookingData.roomsSelected.length === 0) {
      return alert("Vui lòng chọn ngày và phòng!");
    }
    try {
      await axios.post("http://localhost:5000/api/bookings", {
        hotelID,
        user: userInfo,
        rooms: bookingData.roomsSelected.map((r) => r._id),
        checkIn: bookingData.checkIn,
        checkOut: bookingData.checkOut,
        paymentMethod: bookingData.paymentMethod,
        totalPrice: calculateTotal(),
      });
      alert("Booking thành công!");
      window.location.href = "/transactions";
    } catch (err) {
      console.error(err);
      alert("Lỗi đặt phòng");
    }
  };

  if (loading) return <p style={{ textAlign: "center", padding: 50 }}>Đang tải...</p>;
  if (!hotel) return <p style={{ textAlign: "center", padding: 50 }}>Không tìm thấy khách sạn</p>;

  return (
    <div>
      <Navbar />
      <Header type="list" />
      <div className="hotelContainer">
        {open && (
          <div className="slider">
            <FontAwesomeIcon
              icon={faCircleXmark}
              className="close"
              onClick={() => setOpen(false)}
            />
            <FontAwesomeIcon
              icon={faCircleArrowLeft}
              className="arrow"
              onClick={() => handleMove("l")}
            />
            <div className="sliderWrapper">
              <img
                src={hotel.photos[slideNumber]}
                alt={hotel.name}
                className="sliderImg"
              />
            </div>
            <FontAwesomeIcon
              icon={faCircleArrowRight}
              className="arrow"
              onClick={() => handleMove("r")}
            />
          </div>
        )}
        <div className="hotelWrapper">
          <button
            className="bookNow"
            onClick={() => setShowBookingForm(!showBookingForm)}
          >
            Reserve or Book Now!
          </button>
          <h1 className="hotelTitle">{hotel.name}</h1>
          <div className="hotelAddress">
            <FontAwesomeIcon icon={faLocationDot} />
            <span>{hotel.address}</span>
          </div>
          <span className="hotelDistance">
            Excellent location – {hotel.distance}m from center
          </span>
          <span className="hotelPriceHighlight">
            Book a stay over ${hotel.cheapestPrice} at this property and get a free airport taxi
          </span>
          <div className="hotelImages">
            {hotel.photos.map((photo, i) => (
              <div className="hotelImgWrapper" key={i}>
                <img
                  onClick={() => handleOpen(i)}
                  src={photo}
                  alt={hotel.name}
                  className="hotelImg"
                />
              </div>
            ))}
          </div>
          <div className="hotelDetails">
            <div className="hotelDetailsTexts">
              <h1 className="hotelTitle">Stay in the heart of {hotel.city}</h1>
              <p className="hotelDesc">{hotel.desc}</p>
            </div>
            <div className="hotelDetailsPrice">
              <h1>Perfect for a stay!</h1>
              <span>
                Located in the real heart of {hotel.city}, this property has an
                excellent location score of {hotel.rating}!
              </span>
              <h2>
                <b>${hotel.cheapestPrice}</b> (per night)
              </h2>
              <button
                className="bookNow"
                onClick={() => setShowBookingForm(!showBookingForm)}
              >
                Reserve or Book Now!
              </button>
            </div>
          </div>

          {/* Booking Form */}
          {showBookingForm && (
            <div className="bookingFormContainer">
              <div className="bookingFormLeft">
                <h2>Select Dates & Rooms</h2>
                <label>Check-in</label>
                <input
                  type="date"
                  value={bookingData.checkIn}
                  onChange={(e) =>
                    setBookingData({ ...bookingData, checkIn: e.target.value })
                  }
                />
                <label>Check-out</label>
                <input
                  type="date"
                  value={bookingData.checkOut}
                  onChange={(e) =>
                    setBookingData({ ...bookingData, checkOut: e.target.value })
                  }
                />

                <h3>Available Rooms</h3>
                <ul>
                  {availableRooms.map((room) => (
                    <li key={room._id}>
                      <input
                        type="checkbox"
                        value={room._id}
                        onChange={(e) =>
                          handleSelectRoom(room, e.target.checked)
                        }
                      />
                      {room.name} - ${room.price} per night
                    </li>
                  ))}
                </ul>
              </div>

              <div className="bookingFormRight">
                <h2>Your Information</h2>
                <label>Name</label>
                <input
                  type="text"
                  value={userInfo.name}
                  onChange={(e) =>
                    setUserInfo({ ...userInfo, name: e.target.value })
                  }
                />
                <label>Email</label>
                <input
                  type="email"
                  value={userInfo.email}
                  onChange={(e) =>
                    setUserInfo({ ...userInfo, email: e.target.value })
                  }
                />
                <label>Phone</label>
                <input
                  type="text"
                  value={userInfo.phone}
                  onChange={(e) =>
                    setUserInfo({ ...userInfo, phone: e.target.value })
                  }
                />

                <label>Payment Method</label>
                <select
                  value={bookingData.paymentMethod}
                  onChange={(e) =>
                    setBookingData({ ...bookingData, paymentMethod: e.target.value })
                  }
                >
                  <option value="credit_card">Credit Card</option>
                  <option value="paypal">Paypal</option>
                  <option value="cash">Pay at hotel</option>
                </select>

                <h3>Total: ${calculateTotal()}</h3>

                <button onClick={handleReserve}>Reserve Now</button>
              </div>
            </div>
          )}

        </div>
        <MailList />
        <Footer />
      </div>
    </div>
  );
};

export default Hotel;
