import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';
import './transactions.css';

const Transactions = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const token = localStorage.getItem("token");

    useEffect(() => {
        const fetchTransactions = async () => {
            if (!token) {
                setError("Vui lòng đăng nhập để xem giao dịch.");
                setLoading(false);
                return;
            }

            setLoading(true);
            try {
                const res = await axios.get(`http://localhost:5000/api/transactions/me`, {
                    headers: { "Authorization": `Bearer ${token}` },
                });
                setTransactions(res.data);
                setError(null);
            } catch (err) {
                console.error("Lỗi khi lấy giao dịch:", err);
                if (err.response?.status === 401) {
                    setError("Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.");
                } else {
                    setError("Không thể tải lịch sử giao dịch. Vui lòng kiểm tra Server.");
                }
            } finally {
                setLoading(false);
            }
        };

        fetchTransactions();
    }, [token]);

    // Format ngày: 08/11/2025 - 09/11/2025
    const formatDateRange = (start, end) => {
        const options = { day: '2-digit', month: '2-digit', year: 'numeric' };
        const s = new Date(start).toLocaleDateString('vi-VN', options);
        const e = new Date(end).toLocaleDateString('vi-VN', options);
        return `${s} - ${e}`;
    };

    // Format tiền: 1.050.000 ₫
    const formatPrice = (price) => {
        return new Intl.NumberFormat('vi-VN', {
            style: 'currency',
            currency: 'VND'
        }).format(price);
    };

    // Hiển thị trạng thái tiếng Việt
    const getDisplayStatus = (status) => {
        switch (status) {
            case 'Booked': return 'Đã đặt';
            case 'Checkin': return 'Đang lưu trú';
            case 'Checkout': return 'Đã trả phòng';
            default: return status;
        }
    };

    // Class CSS cho badge
    const getStatusClass = (status) => {
        switch (status) {
            case 'Booked': return 'statusBooked';
            case 'Checkin': return 'statusCheckin';
            case 'Checkout': return 'statusCheckout';
            default: return 'statusDefault';
        }
    };

    if (loading) return (
        <>
            <Navbar />
            <p className="loading" style={{ textAlign: 'center', margin: '100px' }}>
                Đang tải giao dịch...
            </p>
            <Footer />
        </>
    );

    return (
        <>
            <Navbar />
            <div className="transactionsContainer">
                <div className="transactionsWrapper">
                    <h1 className="transactionsTitle">Lịch sử Giao dịch của bạn</h1>

                    {error && <p className="error" style={{ color: 'red', fontWeight: 'bold' }}>{error}</p>}

                    {transactions.length === 0 && !error ? (
                        <p>Bạn chưa có giao dịch nào.</p>
                    ) : (
                        <table className="transactionsTable">
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Hotel</th>
                                    <th>Room</th>
                                    <th>Date</th>
                                    <th>Price</th>
                                    <th>Payment </th>
                                    <th>Trạng thái</th>
                                </tr>
                            </thead>
                            <tbody>
                                {transactions.map((t, index) => (
                                    <tr key={t._id}>
                                        <td>{index + 1}</td>
                                        <td className="hotelColumn">{t.hotel?.name || 'Không rõ'}</td>
                                        <td>{Array.isArray(t.room) ? t.room.join(', ') : t.room}</td>
                                        <td>{formatDateRange(t.dateStart, t.dateEnd)}</td>
                                        <td>{formatPrice(t.price)}</td>
                                        <td>{t.payment}</td>
                                        <td>
                                            <span className={`statusBadge ${getStatusClass(t.status)}`}>
                                                {getDisplayStatus(t.status)}
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
            <div className="footer-section">
                <Footer />
            </div>
        </>
    );
};

export default Transactions;
